css:
@-moz-document url-prefix("http://userstyles.org/"), url-prefix("https://userstyles.org/") {
body {
  color: #0b0b0b !important;
  font-family: Arial !important;
  background-color: #e9e9e9 !important;
}
#main-header {
  width: 0 !important;
  height: 0 !important;
  padding-left: 980px !important;
  padding-top: 200px !important;
  background-image: url('http://i1364.photobucket.com/albums/r731/Trewominuby/userstyles_zps9803ca8b.png') !important;
  -webkit-border-radius: 0 0 16px 16px !important;
  -moz-border-radius: 0 0 16px 16px !important;
  border-radius: 0 0 16px 16px !important;
  box-shadow: 0px 4px 0px #cbcbcb !important;
}
#main-header>* {
  display: none !important;
}
#left-sidebar>nav {
  background-color: #f7f7f7 !important;
  -webkit-border-radius: 4px 4px 16px 8px !important;
  -moz-border-radius: 4px 4px 16px 8px !important;
  border-radius: 4px 4px 16px 8px !important;
  box-shadow: 0 2px 0 #d2d2d2 !important;
}
#related {
  background-color: #f7f7f7 !important;
  -webkit-border-radius: 4px 4px 16px 8px !important;
  -moz-border-radius: 4px 4px 16px 8px !important;
  border-radius: 4px 4px 16px 8px !important;
  box-shadow: 0 2px 0 #d2d2d2 !important;
}
input[type="text"] {
  border: 0 !important;
  box-shadow: 0 0 2px #000000 inset !important;
  -webkit-border-radius: 2px 2px 2px 2px !important;
  -moz-border-radius: 2px !important;
  border-radius: 2px 2px 2px 2px !important;
}
input[type="password"] {
  border: 0 !important;
  box-shadow: 0 0 2px #000000 inset !important;
  -webkit-border-radius: 2px 2px 2px 2px !important;
  -moz-border-radius: 2px !important;
  border-radius: 2px 2px 2px 2px !important;
}
input[type="search"] {
  border: 0 !important;
  box-shadow: 0 0 2px #000000 inset !important;
  -webkit-border-radius: 2px 2px 2px 2px !important;
  -moz-border-radius: 2px !important;
  border-radius: 2px 2px 2px 2px !important;
}
input[type="button"] {
  background-color: #ebf5fb !important;
  background-image: url('http://i1364.photobucket.com/albums/r731/Trewominuby/fadewhite_zps79f11b8f.png') !important;
  color: #fefefe !important;
  padding: 4px !important;
  border: 0 !important;
  box-shadow: 0 2px 2px #ebf5fb inset !important;
  -webkit-border-radius: 4px 4px 4px 4px !important;
  -moz-border-radius: 4px 4px 4px 4px !important;
  border-radius: 4px 4px 4px 4px !important;
}
input[type="submit"] {
  background-color: #ebf5fb !important;
  background-image: url('http://i1364.photobucket.com/albums/r731/Trewominuby/fadewhite_zps79f11b8f.png') !important;
  color: #fefefe !important;
  padding: 4px !important;
  border: 0 !important;
  box-shadow: 0 2px 2px #ebf5fb inset !important;
  -webkit-border-radius: 4px 4px 4px 4px !important;
  -moz-border-radius: 4px 4px 4px 4px !important;
  border-radius: 4px 4px 4px 4px !important;
}
button {
  background-color: #ebf5fb !important;
  background-image: url('http://i1364.photobucket.com/albums/r731/Trewominuby/fadewhite_zps79f11b8f.png') !important;
  color: #fefefe !important;
  padding: 4px !important;
  border: 0 !important;
  box-shadow: 0 2px 2px #ebf5fb inset !important;
  -webkit-border-radius: 4px 4px 4px 4px !important;
  -moz-border-radius: 4px 4px 4px 4px !important;
  border-radius: 4px 4px 4px 4px !important;
}
a:link {
  text-decoration: none !important;
  color: #2b5dbc !important;
}
a:visited {
  text-decoration: none !important;
  color: #2b5dbc !important;
}
a:hover {
  text-decoration: none !important;
  color: #2b5dbc !important;
}
a:active {
  text-decoration: none !important;
  color: #2b5dbc !important;
}
}
