css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("narutoforums.com") { 

/** Resize Section - The important stuff
 */

.page-content {
    min-width: 950px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 950px;
    margin-left: auto;
    margin-right: auto;
}

/** Navigation Bar - Those links like "User CP" and "Log Out". 
 */

.subnav {
    max-width: 40%;
    font: bold 7pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;

}

/** Hide the Naruto logo that's floating a bit off to the left
 */
.logo h1 span {
    display: none

}

/** Ad Removal Section - Cannot block links added in text as they're javascript. Use adblock to eliminate those.
 */

.googleadd {
    display: none;

}

.banner-container {
    display: none;

}

.banner1 {
    display: none;

}

.banner2 {
    display: none;
    
}

.content-bdr {
    display: none;

}

.toplinkimg {
    display: none;

}
}
