css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(spiegel.de) {
  body#home {
    background-image: none !important;
    background-color: white
  }
  #spHeader {
    width:       100%  ! important;
    position: absolute !important;
    top: 0;
    left: 0;
  }
  #spColumnAd {
    display: none ! important;
  }
  #spContainer {
    position: absolute ! important;
    top:         130px ! important;
    left:        130px ! important;
  }
  .spBigScreen #spContainer {
    width:       100%  ! important;
  }
  .spSmallScreen #spContainer {
    width:       100%  ! important;
  }
  .spBigScreen #spMainContent{
    width:        80%  ! important;
  }
  .spSmallScreen #spMainContent{
    width:        74%  ! important;
  }
  .spSmallScreen div.spArticle div#spArticleBody {
    width:        95%  ! important;
  }
  #spBreadcrumbLine {
    width:        97%  ! important;
  }
  #spFooter {
    position:            fixed ! important;
    left:                  0px ! important;
    top:                  93px ! important;
    width:               100px ! important;
    background-color:  #efefef ! important;
  }
  .spAssetAligncenter{
    float:                left ! important;
    margin:      0 15px 12px 0 ! important;
  }
  .spAssetAligncenter:after {
    clear:                both ! important;
  }
  .spAssetAligncenter img {
    float:                left ! important;
    margin:                  0 ! important;
  }
  .spMultimediaLeiste {
    width:               50%  ! important;
  }
  div.spMMLBackground  ul {
    width:               100%  ! important;
  }
  .spForumBoxBig {
    width:               40%  ! important;
  }
  .spRessortTeaserBox {
    width:              100%  ! important;
  }
  div#spMainContent div.spDottedLine {
    display: none ! important;
  }
}
