css:
@-moz-document domain("fallout.wikia.com") {
body {
	background: #c9cdac url(http://hydra-images.cursecdn.com/fallout.gamepedia.com/a/ad/Wiki-skin-vector-background.jpg?1);
}
#WikiaPageBackground {
  	-webkit-box-shadow: 4px 8px  #222,  -4px 0 8px #222;   
	-moz-box-shadow: 4px 0 8px #222,  -4px 0 8px #222;   
	box-shadow: 4px 0 8px #222,  -4px 0 8px #222;
}


#WikiHeader .graphic img {
  	content: url(http://static1.wikia.nocookie.net/__cb20120121224609/fallout/images/1/11/NukaPedia_Banner_PNG12-_Rusted.png);
  	repeat: no-repeat;
}
.WikiHeader .navbackground {
  	background: #AD1C13;
    /*background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(35%, #AD1C13), color-stop(65%, #890000));*/
    width: 99.5%;
}
.WikiHeader .navbackground img {
  	display: none;
}
.WikiHeader > nav li.marked {
  	background: #AD1C13;
  	/*background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(35%, #D14037), color-stop(65%, #AD1C13));*/
    border-bottom: 1px solid #AD1C13;
}
.wikia-button, .wikia-menu-button, input[type="submit"], .loginToChat {
  	background: #AD1C13;
  	/*background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(35%, #AD1C13), color-stop(65%, #890000));*/
}
button:hover, .wikia-menu-button > a:hover, input[type="submit"], .loginToChat > a:hover {
  	background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(35%, #AD1C13), color-stop(65%, #890000));
}


tr img {
    max-height: 32px;
}

  
}
