css:
@-moz-document domain("wetterdienst.de") {
#cities, .search_submit, #fb_teilen, #twittern, #searchbox_demo, #footer, .forecast_text, .head_menu,
#left, #skyscraper, .site_menu, #service, .vorhersagemenuline, .location_options, .forecast_rot, .vorhersagemenu, .vorhersagemenuaktiv {
    display: none !important;
}
#detailspopup .vorhersagemenu, #detailspopup .vorhersagemenuaktiv {
    display: block !important;
}
.logo_cont {
    top: 8px !important;
    background: transparent !important;
}
.logo_cont #logo {
    position: relative !important;
    top: 0px !important;
}
.logo_cont #logo img, .logo_cont {
    height: 25px !important;
}

#main, body, #center {
    width: 700px !important;
}
#center {
    margin: 15px 0 0 0 !important;
}
.vorhersagetabelle h1 {
    margin: 0 0 0 15px !important;
}
#center, .tagesauswahl {
    padding: 0 !important;
}
.slider {
    margin-top: 3px !important;
}
body, .vorhersagebox1 {
    background-color: #fff !important;
}
.site_menu {
    background-color: #ddd !important;
}
#detailspopup_overlay {
    background: #ddd !important;
    opacity: 0.7 !important;
}
.forecast_temp {
    font-size: 150% !important;
}
}
