css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("55ch.org") {
body {
    background-color: #FFFFF7 !important;
    }
p {
    display:none;
    }
.footer {
    display:none;
}
.replymode {
    font-family: Tahoma,Verdana,Arial,Helvetica;
    color: #000000 !important;
    background-color: #f2eee0 !important;  
    border-width: thin;
    border-style: solid;
    border-color: #000000;
    }
.reply {
    background-color: #f2eee0 !important;
    }
.postername {
    font-family: Tahoma,Verdana,Arial,Helvetica;
    color: #ffffff !important;
    background-color: #00659c !important;
    }
.logo {
    font-family: tahoma,verdana,arial,helvetica;
    font-weight:bold;
    color: #00659c !important;
}
span[class="filesize"] a {
    font-family: Tahoma,Verdana,Arial,Helvetica;
    color: #0000ff !important;
    background-color: #f2eee0 !important;
    }
a, span[class="filesize"], blockquote, .hidepost {
    font-family: Tahoma,Verdana,Arial,Helvetica;
    color: #800000 !important;
    }
label[class="filesize"], blockquote, .hidepost {
    font-family: Tahoma,Verdana,Arial,Helvetica;
    color: #000000 !important;
    }
.postform {
    background-color: #f2eee0;
    font-family: Tahoma,Verdana,Arial,Helvetica;
    }
.h1 {
    color: #0000ff;
    }
.postblock {
    color: #ffffff !important;
    background-color: #00659c !important;
    border-width: thin;
    border-style: outset;
    border-color: #00659c;
    }
.omittedposts {
    color: #000000 !important;
    font-family: Tahoma,Verdana,Arial,Helvetica;
    }
input[value="Apagar"], input[name="postpassword"], input[name="reportreason"], input[name="reportpost"] {
    color: #ffffff !important;
    background-color: #00659c !important;
    font-family: Tahoma,Verdana,Arial,Helvetica;
    }
a:hover {
    color: #ffffff !important;
    background-color: #00659c !important;
    }

}
