css:
* {
margin: 0;
padding: 0;
border: 0;
outline: 0;
line-height: 1;
vertical-align: baseline;
font: inherit;
text-shadow: 0 -1px 1px rgba(25, 25, 25, 0.25);
box-shadow: 2px 3px 4px rgba(147, 179, 179, 0.25),inset 1px 2px 3px rgba(47, 79, 79, 0.5),inset -1px -2px 3px rgba(147, 179, 179, 0.15);
border-color: rgba(47, 79, 79, 0.5)!important;
}
  
:not(DIV), DIV:not([style]):not([id])  {
background-color: rgba(47, 79, 79, 1)!important;
background-image: -webkit-linear-gradient(45deg, rgba(47, 79, 79, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(147, 179, 179, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(47, 79, 79, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(135deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%);
background-size: 1PX 2PX, 2PX 2PX, 2PX 1PX, 2PX 1PX;
text-shadow: 0 1px 1px rgba(25, 25, 25, 0.52);
box-shadow: 2px 3px 4px rgba(147, 179, 179, 0.25),inset 1px 2px 3px rgba(47, 79, 79, 0.85);
color: rgba(147, 179, 179, 0.85);
}

:not(:empty):not([onclick]):not(SPAN):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([id*="lbImage"]) {
background-color: rgba(47, 79, 79, 1);
background: -webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(47, 79, 79, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(135deg, rgba(47, 79, 79, 0.45) 45%, rgba(0, 0, 0, 0) 45%);
background-size: 2PX 2PX, 1PX 1PX;
  color: rgba(147, 179, 179, 1);
}

SPAN:not(:empty):not([id]),:not(SPAN) {
background-color: rgba(47, 79, 79, 0.75);
background-image: -webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.45) 45%, rgba(0, 0, 0, 0) 45%), -webkit-linear-gradient(-135deg, rgba(47, 79, 79, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.5) 35%, rgba(0, 0, 0, 0) 35%), -webkit-linear-gradient(135deg, rgba(47, 79, 79, 0.45) 45%, rgba(0, 0, 0, 0) 45%);
background-size: 2PX 2PX, 1PX 1PX, 1PX 1PX, 2PX 2PX;
box-shadow: 0 3px 4px rgba(147, 179, 179, 0.5),inset 0 2px 3px rgba(47, 79, 79, 0.35);
border-color: rgba(47, 79, 79, 0.5);
}

html  {
-webkit-font-smoothing:antialiased;
-webkit-transition: color 280ms linear, background 380ms ease-in, box-shadow 380ms ease-out, background-image 480ms linear;
transition:color 200ms, background-color 280ms linear, background 380ms ease-in, box-shadow 380ms ease-out, background-image 480ms linear, text-shadow 250ms;
}

html:root > BODY > * > * > * > *:not(INPUT):not([onclick]) > body {
  color: rgba(147, 179, 179, 1.0);
  background: rgba(0, 0, 0, 0.0);
  background-color: rgba(0, 0, 0, 0.0);
  -webkit-font-smoothing:antialiased!important;
}


ul, ol, li, li > a {
color: lightslategray!important;
background: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-color: royalblue;
background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-size: 100% 1px;
font-weight: bold;
} 

h1, h2, h3, h4, h5, h6 {
color: lightslategray!important;
background: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-color: darkred;
background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-size: 100% 1px;
font-weight: bold;
}

dl, dt, dd, form, fieldset, legend, input, th, td, pre, blockquote, hr {
color: lightseagreen!important;
vertical-align: baseline;
background-color: rgba(0, 0, 0, 0);
background-image: -webkit-linear-gradient(35deg, rgba(147, 179, 179, 0.15) 0%, rgba(0, 0, 0, 0) 50%), -webkit-linear-gradient(-35deg, rgba(147, 179, 179, 0.25) 0%, rgba(0, 0, 0, 0) 50%);
-webkit-font-smoothing: antialiased;
}

a:visited {
background-color: rgb(0, 71, 255);
background-image: -webkit-radial-gradient(center bottom,ellipse cover,rgba(0, 0, 0, 0) 0%,rgba(0, 0, 0, 0.25) 35%,rgba(0, 0, 0, 0.5) 100%);
color: rgba(235, 245, 225, 0.8)!important;
text-decoration: none;
text-shadow: 0 1px 1px rgba(25, 25, 25, 0.5);
-o-transition: color 200ms,background 400ms,box-shadow 250ms;
-moz-transition: color 200ms,background 400ms,box-shadow 250ms;
-webkit-transition: color 200ms,background 400ms,box-shadow 250ms;
transition: color 200ms,background 400ms,box-shadow 250ms;
}

a:link {
background-color: mediumblue;
background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(0, 0, 0, 0) 0%,rgba(0, 0, 0, 0.25) 30%,rgba(0, 0, 0, 0.5) 100%);
color: rgba(224, 234, 224, 0.85)!important;
text-decoration: none;
text-shadow: 0 1px 1px rgba(15, 15, 15, 0.5);
}

a:active, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a {
color: rgb(223, 236, 220)!important;
color: lightslategray!important;
background: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-color: rgba(0, 0, 0, 0);
background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-size: 100% 1px;
font-weight: bold;
}

a:focus {
color: rgba(217, 231, 243, 0.8)!important;
text-shadow: 0 -1px 0 rgba(5, 5, 5, 0.5);
outline: none;
background: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-color: rgb(0, 128, 128);
background-image: -webkit-radial-gradient(center center,ellipse cover,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 65%);
background-size: 100% 1px;
text-decoration: underline;
}

a:hover{
  color:rgba(255,255,255,1)!important;
  text-shadow:0 1px 1px rgba(55,55,55,0.5);
  -o-transition:color 200ms,background 400ms,box-shadow 250ms;
  -moz-transition:color 200ms,background 400ms,box-shadow 250ms;
  -webkit-transition:color 200ms,background 400ms,box-shadow 250ms;
  transition:color 200ms,background 400ms,box-shadow 250ms;
}

a > img {
border-collapse: separate;
border-spacing: 0;
padding: 1px 1px;
box-shadow: 0 0 15px 1px rgba(255, 255, 255, 0.5);
background: -webkit-radial-gradient(center,ellipse cover,rgb(238, 248, 255) 68%,rgb(0, 0, 0) 98%);
vertical-align: -webkit-baseline-middle;
}
/*still need 'cellspacing="0"' in the markup */

img[alt^="Fuck"], img {
border-collapse: collapse;
border-spacing: 0;
padding: 1px 1px 2px;
box-shadow: 3px 4px 10px -2px rgba(28, 28, 28, 0.85);
background: -webkit-radial-gradient(center,ellipse cover,rgb(0, 0, 0) 47%,rgb(255, 245, 235) 93%);
vertical-align: -webkit-baseline-middle;
}

input[type="button"], input[type="submit"], input[type="reset"]  {
-webkit-appearance: push-button;
white-space: pre;
background-color: rgba(107, 246, 13, 0.5)!important;
color: rgb(0, 241, 126)!important;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
box-sizing: border-box;
}
  
input, textarea, keygen, select, button, isindex {
white-space: pre;
-webkit-appearance: textfield;
background: rgba(255, 255, 255,0.65);
background-color: rgba(255, 255, 255,0.65)!important;
color: rgb(29, 32, 28)!important;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
font-style: italic;
box-sizing: border-box;
}
  
abbr, acronym, address, big, cite, del, dfn, font, ins, kbd, q, s, samp, small, strike, sup, tt, var, b, u, i, dl, dt, dd, label, strong {
font-weight: bold;
color: ghostwhite!important;
background: -webkit-linear-gradient(45deg, rgba(47, 79, 79, 0.5) 50%, rgba(255, 255, 255, 0.75) 50%), -webkit-linear-gradient(-45deg, rgba(47, 79, 79, 0.5) 50%, rgba(255, 255, 255, 0.75) 50%);
background-size: 2px 2px;
}
  
center, p, span, td {
color: mintcream!important;
background: -webkit-linear-gradient(45deg, rgba(147, 179, 179, 0.5) 50%, rgba(255, 255, 255, 0.25) 50%), -webkit-linear-gradient(-45deg, rgba(147, 179, 179, 0.5) 50%, rgba(0, 0, 0, 0.75) 50%);
background-size: 3px 3px;
}
