css:
@-moz-document url-prefix("http://huaban.com/gift/") {
#gift_goods {
background: #fff;
}
.pin {
box-shadow: 0 0 0 0 rgba(0,0,0,0) !important;
}
.pin:before {
content: "";
display: block;
position: absolute;
top: 0;
left: 0;

width: 100%;
z-index: 9999;
height: 100%;
box-shadow: inset 0 0 0 1px rgba(0,0,0,0.07);
}

#gift_goods .gift-filter {
background: #fff;
box-shadow: inset 0 0 0 2px rgba(0,0,0,.05);
margin: 20px 0 16px;
}

#gift_goods .gift-filter .filters .filter .sub-words {
height: 44px;
line-height: 44px;
margin-top: -11px;

}
}
