css:
/* hidden windows frame border &/ titlebar and min-max-close buttons */
/* 去掉windows窗口边框，标题栏和/或最大最小和关闭按钮 */
/* for Stylish or userChrome.css */
/* for Windows 7 */
/* 20140214 */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://browser/content/browser.xul) {

#main-window/*[sizemode="maximized"]/**/[sizemode="normal"]/**/ {
   -moz-appearance:none !important/**/;
   /*-moz-appearance:-moz-win-borderless-glass !important;/**/
   /*-moz-appearance:-moz-win-glass !important;/**/
   }

#main-window[sizemode="maximized"]/**//*[sizemode="normal"]/**/ {
   /*-moz-appearance:none !important;/**/
   /*-moz-appearance:-moz-win-borderless-glass !important;/**/
   -moz-appearance:-moz-win-glass !important;/**/
   }

#main-window[sizemode="normal"] #titlebar {display: none !important;}

#main-window #titlebar-buttonbox {
   /*-moz-appearance:none !important;/**/
   /*-moz-appearance:-moz-win-borderless-glass !important;/**/
   /*-moz-appearance:-moz-win-glass !important;/**/
   }

#main-window #mainPopupSet {
   /*-moz-appearance:none !important;/**/
   /*-moz-appearance:-moz-win-borderless-glass !important;/**/
   -moz-appearance:-moz-win-glass !important;/**/
   }

}
