css:
@-moz-document domain("play.google.com") {
/* Screenshots */
.screenshots .expand-pages-container .expand-page .thumbnails,
.FaSaxe {
  white-space: normal !important;
}
.expand-pages-container,
.details-section.screenshots .thumbnails-wrapper,
.details-section.screenshots .thumbnails,
.XYLQ2b {
  height: auto !important;
}
.details-section.screenshots .thumbnails-wrapper {
  margin-bottom: 0;
}
.details-section.screenshots .thumbnails {
  overflow: visible !important;
  overflow-x: visible !important;
  overflow-y: visible !important;
  padding-bottom: 0;
}
.details-section.screenshots .thumbnails .screenshot {
  margin-bottom: 5px;  /* was 40px */
}

.details-section.screenshots .expand-page + .expand-page {
  height: 0  /* Is overriden when a screenshot is selected. */;
}

.KDxLi {
  transform: none !important;
}

/* Reviews */
.reviews .preview-panel .multicol {
  overflow: visible !important;
  overflow-x: visible !important;
  overflow-y: visible !important;
}
/*
.reviews .expand-page {
  opacity: 1 !important;
  display: inline !important;
}
.reviews .multicol-column {
  display: inline !important;
}*/
}
