css:
@-moz-document domain(forum.te.ua) {

.smallfont > strong,
form[action="index.php"],
center > table:last-child,
td.smallfont,
div.advertisement,
td[colspan="5"] {
    display: none;
}

td.alt1Active,
td.alt2, td.alt1 {
    border-top: 1px solid #000;
}

td.tcat,
.thead {
    background: none;
}

.tborder {
    border: none;
}

body {
    margin: auto;
    filter: invert(0.8) hue-rotate(180deg) contrast(1.4) saturate(1.2);
  -webkit-filter: invert(0.8) hue-rotate(180deg) contrast(1.4) saturate(1.2);
}

}
