css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theoldreader.com") {
  .navbar-fixed-top {
     top: -50px !important;
  }
  
  .container-fluid {
     top: 17px !important; /* 67 - 50 */
  }

  .navbar-fixed-top:hover {
     top: 0px !important;
  }
  
  .navbar-fixed-top:hover + .container-fluid {
     top: 67px !important;
  }  
}
