@-moz-document domain("bs.to") {
/*0. Links*/
.home a {
    color: #00FF00!important;
}

.home #shoutbox a.mod {
    color: #555555 !important;
}
.home #shoutbox a.smod {
    color: #555555 !important;
}
/*0. End*/

/*1. Background + Schriftfarbe*/
body > #root {
    background: url("https://i.imgur.com/4rFx6er.jpg") no-repeat scroll 0 0 rgb(19, 43, 114) !important;
    color: #96fa00 !important;
    margin-bottom: 20px !important;
    margin-top: 20px !important;
    box-shadow: 0 0 7px 0 #FFFFFF !important;
}
/*1. End*/

/*2. User Box*/
#user {
    background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}

#user > fieldset input[type=submit] {
    background-color: rgb(200, 0, 0) !important;
}
#user > fieldset input[type=text] {
    background-color: rgb(230, 0, 0) !important;
    color: #FFFFFF !important;
}
#user > fieldset input[type=password] {
    background-color: rgb(210, 0, 0) !important;
    color: #90FA00 !important;
}
/*2. End*/

/*3. Teaser*/
.home #globalNotification a {
    color: #000 !important;
}
.home > .column > section, .home > #teaser {
    background-color: #007700 !important;
    background-image: linear-gradient(#009900 0px, #007700 24px) !important;
    border: 1px solid #999999 !important;
    border-top-left-radius: 15px !important;
    border-top-right-radius: 15px !important;
    box-shadow: 0 0 7px #FFFFFF !important;
}
/*3. End*/

/*4. Teaser Background*/
.home > .column > section > div, .home > #teaser > div {
    background-color: #000077 !important;
    color: #96FA00 !important;
}
/*4. End*/

/*5. Shoutbox*/
.home #shoutbox dl {
    max-height: 800px !important;
}
.home #shoutbox dl > dt {
    background-color: rgb(60, 176, 223) !important;
}

.home #shoutbox dl > dt time {
    color: rgb(255, 255, 255) !important;
}

.home #shoutbox a.user {
    color: #FFFFFF !important;
}

.home #shoutbox {
    color: #96FA00 !important;
}
.home #shoutbox a.smod {
    color: #00FF00 !important;
}
.home #sbUser {
    background-color: rgb(60, 176, 223) !important;
    border-radius: 10px !important;
    box-shadow: 0 0 7px 0 #FFFFFF !important;
    color: #000000 !important;
    padding: 10px !important;
}
/*5. End*/

/*6. Neuste Serien*/
.home li:nth-child(2n) {
    background-color: rgb(60, 176, 223) !important;
    color: #FFFFFF !important;
}

.home #newest_series a {
    color: #FFFFFF !important;
}
/*6. End*/

/*7. News*/
.home #news {
    color: #96FA00 !important;
}

.home #news a {
    color: #FFFFFF !important;
}
/*7. End*/

/*8. Footer*/
#root > footer {
    border-top: 1px solid #999999 !important;
    background-color: rgb(49, 83, 154) !important;
    border-bottom-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
    color: #96FA00 !important;
}

#root > footer a {
    color: #FFFFFF !important;
}
/*8. End*/

/*001. Serien*/
/*9. Message-Box*/
.messageBox.info {
    color: #000000 !important;
}
/*9 End*/

/*10. Serien (sp_left)*/
#sp_left a {
    color: #FFFFFF !important;
}

.pages > .button > a {
    background-color: rgb(23, 121, 164) !important;
}

.pages > .button > a:hover {
    background-color: rgb(61, 179, 227) !important;
}

.serie .watched {
    background-color: #559922 !important;
}

.pages > li > a:hover {
    background-color: rgb(61, 179, 227) !important;
}

.pages > .current {
    background-color: #6666fa !important;
}

.serie .epiInfo {
    background-color: rgb(61, 179, 227) !important;
    border: 1px solid #999999 !important;
    box-shadow: 0 0 7px 0 #FFFFFF !important;
    border-radius: 10px !important;
    color: #000000 !important;
}
/*10. End*/

/*11. sp_right*/
#sp_right a {
    color: #FFFFFF !important;
}

tr:nth-child(2n) {
    background-color: #555599 !important;
}

#seriesdata td ul li:hover:not(.noentry) {
    background: none repeat scroll 0 0 rgb(61, 179, 227) !important;
    color: #000000 !important;
}
/*11. End*/

/*001. End*/


/*002. Team*/
.team dl a {
    color: #FFFFFF !important;
}

.team dl {
    box-shadow: 0 0 7px 0 #FFF !important;
    padding: 5px !important;
    background: rgb(61, 149, 57) !important;
    border-radius: 10px !important;
}

.team em a {
    color: #FFFFFF !important;
}
/*002. End*/

/*003. FAQ*/
.faq dt:target {
    background: none repeat scroll 0 0 #0099ff !important;
}

.faq dt:target + dd {
    background: none repeat scroll 0 0 #555599 !important;
}

.faq dt:target + dd a{
    color: #FFF !important;
}

.faq dt a {
    color: #FFF !important;
}

.faq dt a:hover {
    font-weight: bold !important;
}

.faq dt:hover {
    background-color: rgb(31, 156, 209) !important;
}
/*003. End*/

/*004. Regeln*/
.regeln a{
    color: #FFF !important;
}
.regeln #globalNotification a {
    color: #000000 !important;
}
/*004. End*/

/*005. Statistik*/
#hoster_cart tspan a {
    color: #FFF !important;
}
/*005. End*/

/*006. Spenden*/
.donation input, textarea, select {
    background: rgb(19, 43, 114) !important;
}

.donation a {
    color: #FFF !important;
}

.donation #globalNotification a {
    color: #000000 !important;
}
/*006. End*/

/*007. Random*/
.random input, textarea, select {
   background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}
/*007. End*/

/*008. Suche*/
.suche input, textarea, select {
   background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}
/*008. End*/

/*009. App*/
.app a {
    color: #FFF !important;
}
/*009. End*/

/*010. Statistik*/
/*Muss noch gemacht werden!*/
/*010. End*/

/*011. Andere Serien*/
.andere-serien input, textarea, select {
   background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}

.andere-serien a {
    color: #FFFFFF !important;
}

.andere-serien #globalNotification a {
    color: #000000 !important;
}
/*011. End*/

/*012. Vorgeschlagene-Serien*/
.vorgeschlagene-serien input, textarea, select {
   background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}

.vorgeschlagene-serien a {
    color: #FFFFFF !important;
}

.vorgeschlagene-serien #globalNotification a {
    color: #000000 !important;
}
/*012. End*/
/*013. User Center*/
.user-center #nav {
    border-radius: 10px !important;
    padding-left: 2px !important;
    border: 1px solid #999 !important;
    box-shadow: 0 0 7px 0 #FFF !important;
    background: rgb(61, 149, 57) !important;
}

.user-center #nav a {
    color: #FAFAFA !important;
}

.user-center .col {
    border-radius: 10px !important;
    padding-left: 2px !important;
    border: 1px solid #999 !important;
    box-shadow: 0 0 7px 0 #FFF !important;
    background: rgb(61, 149, 57) !important;
    margin-top: 5px !important;
    margin-left: 6px !important;
}

.user-center .col li {
    background: rgb(61, 189, 57) !important;
    color: #FFF !important;
}

.user-center #usrCnt input, textarea, select {
   background-color: rgb(27, 168, 77) !important;
    color: #000000 !important;
}
/*013. End*/
}
