css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wx.qq.com") {
    *{text-shadow: none !important;}
    body{background: #151515 !important;}
    img{opacity:0.7;}
    
    .systemTip{background-color: #000000 !important;}
    .groupTitle{background-color: #151515 !important;}
    
    div.chatScorll{background-color: #151515 !important;}
    div.chatListColumn{background-color: #151515 !important;}
    div.name{font-weight:bold; color: #C7EDFF !important; }
    div.content{background-color: #151515 !important;}
    div.media{ background: #555555 !important;}
    div.listBarContent{ background: #151515 !important;}
    div.operaterBox{ background: #151515 !important;}
    div.header{ background: #888888 !important;}
    div.inputArea{ background: #151515 !important;}
    div.chatListSearch{ background: #151515 !important;}
    div.chatListSearchInput.left{ background: #151515 !important;}

    a.friendDetail{color: #C7EDCC !important; background-color: #151515 !important;}
    
    span{color: #C7EDCC !important;}
    
    p{color: #C7EDCC !important;}
    p.chatName{font-weight:bold; color: #C7EDFF !important;}
    p.left{font-size: 120% !important;}
    
    textarea{color: #C7EDCC !important; background: #151515 !important;}
    input{color: #C7EDCC !important; background: #151515 !important;}
}

@-moz-document domain("mp.weixin.qq.com") {
    *{color: #C7EDCC;}
    *{font-family: DFPYuanW5-GB !important;}
    *{text-shadow: none !important;}
    
    .rich_media_inner{background: #000 !important;}
    
    body{background: #151515 !important;}
    a{color: #C7EDFF;}
    img{opacity:0.7;}
    
    h1{color: #C7EDCC !important;}

    p{
        color: #C7EDCC !important; 
        background: #000 !important;
        font-size: 1.3em !important;
    }
    
    span{
        font-size: 1em !important;
        color: #C7EDCC !important; 
        background: #000 !important;
    }
}
