css:
@-moz-document domain("badoo.com") {
#sb_folders {
  margin: 0 0 20px -20px;
  padding: 14px 20px 20px;
  border: 1px solid #CAC9C9;
  border-radius: 6px;
  background: #fff;
  text-align: left;
  box-shadow: 5px 5px 15px #888;
}

.pageblock--stroke,.sideblock--stroke {
  border: 1px solid #C9C9C9;
}

#credits_holder.hai.hai-c.jsw {
}

#wrap .search-header {
  padding: 0;
}

#wrap1 {
  background-color: rgba(238,238,238,0.75);
  margin: 0 auto;
  padding-bottom: 0;
  position: relative;
  text-align: center;
}

.userlist {
  bottom: 0 px;
}

.ready_to_chat .is_special {
  display: none;
}

.locut_container {
  white-space: normal;
}

.badge {
  border: 1px solid;
  border-radius: 10px;
  box-shadow: 2px 2px 2px rgba(0,0,0,0.345), 0 1px rgba(255,255,255,0.298) inset;
}

.badge__attr {
  border-top: 2px solid;
  border-radius: 0 0 9px 9px;
  background: linear-gradient(tobottom,#000000,#000000) transparent;
}

.intr {
  text-align: center;
  padding: 2px;
  border: 1px solid #888;
  margin: 3px 3px 0;
  border-radius: 5px;
  box-shadow: 0 0 4px #888;
}

.intr.intr--match {
  box-shadow: 0 0 5px red;
}

.intr--match .intr-txt {
  font-weight: 700;
}

.badge__attr .dstc {
  color: #FFF;
}

#sb_spp_button,#spotlight,#logo,#spp_badge,#block_rating_scale,#footadver,.footer,.pid.pid_lowest,.search-header__briefing.form_toggle
.rbb_blue,#ru_block,.pid,#gifts,.other_popularity,.popularity,.footer-fake {
  display: none;
}

.user-card {
  box-shadow: 5px 5px 5px rgba(131,82,84,0.43);
  margin: 0 0 20px;
  height: 325px;
  text-align: left;
  border-radius: 5px;
}

.user-card__section {
  margin: 0 0 20px;
  height: 54%;
  text-align: left;
  position: relative;
}

.pager-and-found {
  font: 20px Helvetica,Arial,sans-serif;
}

.search-common {
  padding-bottom: 0;
}

.user-card__attr {
  position: absolute;
  bottom: 0;
  width: 97%;
  min-height: 20px;
  padding: 3px;
  border-radius: 0 0 6px 6px;
  background: #f7f8fa;
}

.user-card__section .user-name {
  margin: -2px 0;
  text-align: center;
  width: 100%;
}

.user-icon {
  margin: 0;
}
}
