css:
@-moz-document domain("magistrix.de") {
#bigsize-top, .breadcrumb, .btn-toolbar, .navbar, .hidden-xs, .footer, .sidebar, #js-comments,
#qmn, #skyscraper-right, .js-complain, .user-link, .footer-link, .artist-nav-image, .artist-nav-toggle, .lyric-content div div {
    display: none !important;
}
.site-background-image, body, .artist-nav-link, .visible-xs-sm {
    background: none #fff !important;
    border: none !important;
}
.col-md-8, .col-lg-10 {
    width: 100% !important;
}
.container {
    padding: 40px !important;
}
a {
    text-decoration: none !important;
    color: #000 !important;
}
}
