css:
@-moz-document domain("animeultima.tv") {
body {
text-align: center;
font-family: Arial, Helvetica, sans-serif;
font-size: 12px;
background: #000;
margin: 0px;
color: #999999;
}
body, html {
margin: 0px;
padding: 0px;
}
user agent stylesheetbody {
display: block;
margin: 8px;
}


ul#left-nav li:hover ul {
display: block;
}
ul#left-nav li ul {
display: none;
position: absolute;
z-index: +1;
border: 1px solid #ccc;
margin: 3px;
background: #333366;
opacity: 0.9;
padding: 5px;
width: 150px;
}



#parent-container, #search-container 
{
-moz-border-radius: 8px;
-webkit-border-radius: 8px;
background: #0f0f0f;
margin: 15px auto 15px auto;
opacity: 0.8;   
}
.generic-padd {
padding: 15px;
}
.generic-container {
width: 975px;
margin: 0 auto;
text-align: left;
}



#header-span 
{
background: url("http://imageshack.com/a/img843/4144/g7i1.jpg");
padding-bottom: 0px;
border-bottom: 2px solid #9abccd;
}
  
  
#header-container {
width: 975px;
margin: 0 auto;
text-align: left;
background: #000;
padding-top: 10px;
}
  

#header-left {
float: left;
background: url("http://imageshack.com/a/img689/7536/mkv0.jpg");
width: 220px;
height: 73px;
text-align: center;
display: none; 
  }
  


ol.standardlist .title a {
color: #fff;
text-decoration: none;
}



div.comment-item 
{
color: #fff;
}


ul.stripes li:hover {
background: #0f0f0f;
}

ul.stripes li {
background: #000;
padding: 10px;
border-bottom: 1px solid #ddd;
}


div.comment-item-staff {
background: #0e3302;
}



ul#hot-shows li.li-label {
font-weight: bold;
padding: 5px !important;
color: #fffb00;
background: #e75219;
}
}
