css:
@-moz-document url-prefix("https://reader.aol.com/"), url-prefix("http://reader.aol.com/") {
#reader-container {
	margin-right: 0px !important;
	margin-top: 0px !important;
}

.content-region {
	margin-left: 20px !important;
	margin-top: 0px !important;
}
  
.section-header {
	left: 0px !important;
	top: 0px !important;
}

.feed-header{
	right: 0px !important;
}

.header {
	display:none !important;
}
  
.article-item-full .article-content {
	width: auto !important;
}

.nav, .feed-header{
	top: 0px !important;
}
  
#reader-container {
	margin-left: 0 !important;
}

.feed-header {
	left: 0px !important;
}

.nav {
    top: 49px !important;
    border-right: red 5px solid 1important;
	margin-left: -260px !important;
    padding: 30px 20px 20px !important;
    position: fixed !important;
    z-index: 200;
    -moz-transition-duration: .5s;
    -webkit-transition-duration: .5s;
}

.nav:hover {
    margin-left: 0 !important;
    left: 0 !important;
    opacity: 1 !important;
    -moz-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
    -webkit-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
    box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
}

.nav-list {
  	width: 90% !important;
    top: 10px !important;
}
}
