css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
#main-header{background-color: transparent!important;font-family:Open Sans Light!important; font-size: 19px!important; text-align: center; text-decoration:none!important;}
#main-header a{color:rgb(0,0,255)!important;text-decoration:none!important;}#main-header a:hover{color: rgb(120,120,255)!important;}
#left-sidebar a,#show-advanced-search,footer a{color:rgb(100,100,100)!important;text-decoration:none!important;}#left-sidebar a:hover,#show-advanced-search:hover,footer a:hover{color: rgb(100,100,255)!important;}
a {text-decoration:none!important;}
body {font-family: open sans!important;}
footer {text-align: center!important;}
#left-sidebar > *  {margin-bottom: 10px;
border: 1px solid rgb(221, 221, 221);
padding: 5px;
border-radius: 5px;
background-color: transparent!important;}
h3,h2, #more-screenshots span, th{font-family:Open Sans Light!important;
text-decoration:none!important;font-weight:600;}
th {font-size:13px!important}
.install {background-image: linear-gradient(rgb(60, 150, 60), rgb(60, 150, 74))!important;}
.install:hover {background-image: linear-gradient(rgb(100, 150, 100), rgb(100, 160, 114))!important;}

a:link {color:rgb(0,0,255)!important; text-decoration: underline; }
a:active {color: #0000ff!important; text-decoration: underline; }
a:visited {color: rgb(80,80,80)!important;}
a:visited:hover {color: rgb(120,120,120)!important;}
a:hover {color: rgb(100,100,255)!important; text-decoration: none; }
    
DIV#stylish-installed-style-installed.install-status.install-info div {height: 0px!important;
    width: 0px!important}
A.install-note{color: rgb(211,211,211)!important;}
    A.install-note:hover{color: rgb(230,230,230)!important;}

}
