css:
@-moz-document url-prefix("http://www.deviantart.com/messages/") {
.mcbox-bigthumb .mcb-description, .mcbox-bigthumb .mcb-app {display: none !important;}

.mcbox-bigthumb {display: inline-block !important; margin-right: 10px !important; margin-bottom: 10px  !important;}

.mcbox-bigthumb .f {width: auto !important;}
td .f>div {margin: 0 !important;}
.mcbox-bigthumb .gmbutton2 {margin: 5px 14px 5px 5px !important;}
.mcbox-bigthumb .mcb-favecount {margin-left: 9px !important;}

.mcbox-bigthumb {vertical-align: middle !important;}

.mcbox-bigthumb br {display: none !important;}
.mcbox-bigthumb .hh {padding-top: 16px  !important; padding-bottom: 3em !important;}
.mcbox-bigthumb .mcb-tab {padding-top: 2.75em !important;}

.mcbox-bigthumb .cf:before {display: none !important;}
.mcbox-bigthumb .cf:after {display: none !important;}
.mcbox-bigthumb .mcb-whoicon {display: none !important;}
.mcbox-bigthumb .cf {font-size: 8pt !important; font-weight: normal !important;}
.mcbox-bigthumb .mcb-title a {font-size: 8pt !important; display: inline !important; font-weight: normal !important;}
.mcbox-bigthumb .mcb-line_huge a, .mcbox-bigthumb .mcb-line a {font-weight: normal !important;}
.mcbox-bigthumb .mcb-line_huge {line-height: 1 !important;}

.mcbox-bigthumb .cf {position: absolute !important; bottom: 1em !important; left: 0px !important; width: 100% !important; text-align: center !important;}

.mcbox-bigthumb .f img {box-shadow: 0 0 0 1px rgba(0,0,0,0.1), 0 1px 1px rgba(0,0,0,0.2), 0 2px 2px rgba(0,0,0,0.2)}
}
