css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.hardwareluxx.de") {

h1, h2, h3, h4, h5, h6, .ui-body-c, .ui-overlay-c, h1.overview-header > a.ui-link, h1.overview-header > a.ui-link:visited, h1.overview-header > a.ui-link:active, h2.overview-header > a.ui-link, h2.overview-header > a.ui-link:visited, h2.overview-header > a.ui-link:active
{
font-family: Roboto,"Droid Sans",Calibri,Helvetica,Arial,Verdana,sans-serif !important;
}

h2, .ui-body-c h2 .ui-link, .ui-body-c h2 .ui-link:visited {
    color: #AE0000;
    font-size: 16px;
text-decoration:none !important;
}
.ui-body-c h2 .ui-link:hover, .ui-body-c h2 .ui-link:active
{
text-decoration:underline !important;
}

h1.overview-header
{
/*background:black !important;*/
margin:0 -10px 0  -10px !important;
padding:20px 10px 10px 10px !important;

}
.ui-header .ui-title
{
border:none !important;
}
h1 
{
text-align: center !important;
    border-bottom:solid 2px #E4E4E4 !important;

}
h1.overview-header > a
{
text-decoration:none !important;
border:none !important;
text-transform: uppercase;
}

h2, .ui-body-c h2 .ui-link, .ui-body-c h2 .ui-link:hover, .ui-body-c h2 .ui-link:active, .ui-body-c h2 .ui-link:visited
{
margin: 5px 0 !important;
font-size:1.15em !important;
}

div.hwl_mobile_article
{
padding-bottom:10px !important;
border-bottom:1px solid #F0F0F0;
}

ul.plain-list 
{
margin-left:10px !important;
list-style-type:square !important;

}
ul.plain-list li
{
margin-top:10px !important;
}

div.item-page p
{
    font-size: 1em !important;
    line-height: 1.4em !important;
}
.item-page > p > strong > img, .item-page > p > img
{
display:none !important;
}

}
