css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hootsuite.com"){

/* MessageBox */
.messageBoxContainer .messageBox .composerBox {
  height: 250px !important;
}

.messageBoxContainer .messageBox .composerBox div {
  height: 250px !important;
}

.messageBoxContainer .messageBox .composerBox .ac_input div {
  height: 250px !important;
  max-height: 250px !important;
}

.messageBoxContainer.snvariant1 .messageBox .composerBox div {
  height: 250px !important;
  max-height: 250px !important;
}

.messageBoxContainer.collapsed.snvariant1 .composerBox div{
  height: 22px !important;
}
  
.messageBoxContainer.collapsed .composerBox{
    height: 18px !important;
}

.messageBoxContainer.collapsed .composerBox div{
    height: 22px !important;
}

.messageBoxContainer.collapsed .composerBox .ac_input div{
    height: 22px !important;
}

/* Message box when writing a Comment */
.message .messageComments .commentEntry textarea.focus, .messageComments.x-facebook .commentInput textarea.focus {
    height: 150px !important;
}
  
}
