@-moz-document domain("dogecharts.org") {
table#buy_orders td {color: darkgrey!important;}
table#sell_orders td {color: darkgrey!important;}

table#buy_orders .price { color: green!important; }

table#sell_orders .price { color: red!important; }


table#buy_orders .price + .total { color: black!important; }

table#sell_orders .price + .total  { color: black!important; }
}
