css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockfm.ru") {
    .radio_data_field{
        position:absolute !important;
        z-index:99999 !important;
        background:#fff !important;
        color:#000 !important;
        width:100% !important;
        margin:0 !important;
        left:0 !important;
        height:100% !important;
        top:0 !important;
        text-align:center !important;
    }
    .radio_data_field span, .radio_data_field b{
        line-height:200%;
        font-size:60px;
     }
     html,body{
        height:100% !important;
        overflow:hidden !important;
     }
     .right-field,iframe{display:none !important;}

}
