css:
@-moz-document url-prefix("http://blog.livedoor.jp/dqnplus") {

  #title, #left, #right, .google-user-ad, #articletop,
  [class*="popular"], .pager, .side-navi, #ld-topics,
  #poweredAdsBy, .author, .menu, [class*="related"],
  .amazlet-box, center a, #takecomment, #formbody
  { display: none !important; }

  #container {
    background: none;
    background-color: #eee;
    margin: 0;
    padding: 0;
    width: auto;
  }
  #blogcontainer, #blog {
    float: none;
    width: 768px;
    margin: 40px auto;
  }
  #wrapper, #content {
    float: none;
    width: auto;
  }
  .fullbody, #articlebody {
    background: none;
    background-color: #f8f8f8;
    padding: 40px;
    border-radius: 4px;
    box-shadow: 0 1px 4px #ddd;
  }
  .dateend, #articlebottom {
    background: none;
  }

}
