css:
/*=====================================================
  BIGLOBEニュース domain(news.biglobe.ne.jp)
  =====================================================*/
@-moz-document domain("news.biglobe.ne.jp"){

/*--- unwanteds ---*/
#SPbanner,
dummy#dummy.dummy {display: none !important;}

/*--- page containers ---*/
#pg {width: auto !important; text-align: left !important;}


/*--- header ---*/
#hd_1 {padding: 0 !important;}
#hd_L { margin: 0 !important; padding: 0 !important;}
#hd_R * {
 width: auto !important; height: auto !important;
 margin: 0 !important; padding: 0 !important;
 }

/*--- tenki ---*/
#tenki_parts {
 width: auto !important; height: auto !important;
 margin: 0 !important; padding: 0 !important;
 }
#tenki_parts * {
 display: inline-block !important;
 width: auto !important; height: auto !important;
 margin: 0 !important; padding: 0 !important;
 }

/*--- contents navi ---*/
#cnr {box-sizing: border-box; margin: 0 !important;}
}
