@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dilbert.com") {
    #meebo-canvas-3,
    #meebo-iframe,
    .Ad_Banner,
    .Col_B,
    .STR_StripComments,
    .STR_AdBlock,
    .STR_Footer,
    .STR_Search,
    .STR_Zoom,
    .Header,
    .HDR_Home,    
    .HDR_Nav,
    .FTR_FooterTop,
    .FTR_Footer,
    .GLBL_PromoU,   
    .TTL_Title,
    .ui-datepicker-trigger,
    [class^=HPG_Promo]{
        display:none !important;
    }

    body {
        background: none !important;
        width: 1px !important;
    }
    .SiteWrapper{
        /*margin:0 !important;*/
        width: 1px;
    }
    .Content{
        padding: 0 !important;
    }
    .TransparentBackground{
        margin:0 !important;
    }    
    .Content, .Col_A{
        float: none !important;
    }
    .STR_Header, .STR_Content{
        background: none !important;
    }
    .STR_Calendar{
        margin-left: auto !important;
        margin-right: auto !important;
        margin-left: 35% !important;
        margin-right: 35% !important;       
    }
}
