css:
@-moz-document domain("tweetdeck.twitter.com") {
   * {border-radius: 0 !important;}
    .scroll-styled-v {overflow-y: hidden !important;}
    .scroll-styled-v:hover {overflow-y: auto !important;}
    .scroll-styled-h {overflow-x: hidden !important;}  
   html, .tweet-body {line-height: 1.2 !important;}
   div.column-content {top: 30px!important;}
   .column-header, .column-header-temp {height: 30px!important;}
   .txt-sub-antialiased {font-size: 12px!important;}
   .sprite-drag-vertical {height: 13px!important;}
   .column-title-back .icon {top: 12px!important;}
   .column-header, .column-header-temp {line-height: 25px!important;}
   .column-header-link .icon-sliders {vertical-align: 0px!important; font-size: 21px!important;}
   .compose-title {padding: 0px !important;}
   .compose-header {height: auto !important;}
   .compose-accounts {margin: 5px 0px !important;}
   .tooltip {display: none !important;}
   .padding-hl {padding: 0px 10px !important;}
   .column{margin: 0.5px !important; padding: 0px !important;}
   .txt-base-smallest {font-size: 12px;}
   .column-type-icon {display:none;}
   .is-actionable {cursor: default;}
   .column-title {
      padding-left:6px;
      font-size: 18px;
      text-align: left;
      font-weight: 100;
   }
}
