css:
/* i really want this to be global */
/* all block elements  */
body, form, header, html, span, div, table, tr, td, th, p, b, ul, li, h1, h2, h3, h4, h5, figcaption, figure, b, b font, pre
{
    color: #CCCCCC !important;
    background: #252525 !important;
    border-color:#000 !important;
}

/* inputs */
input, textarea
{
   color: #CCCCCC !important;
   background: #252525 !important;
}
/* wiki text images - eg formulas */
img.tex
{
  background: #FFFFFF !important;
}

/* sme.sk */
div.dxse_commpages span
{
    color: #990000 !important;
    background: #252525 !important;
    border-color:#000 !important;
}
.reaction_piano
{
    color: #CCCCCC !important;
    background: #303030 !important;
    border-color:#000 !important;
}
.ranking
{
    /* Fallback for web browsers that doesn't support RGBa */
    background: rgb(0, 0, 0) transparent !important;
    /* RGBa with 0.6 opacity */
    background: rgba(0, 0, 0, 0) !important;
    /* For IE 5.5 - 7*/
    /* NOTE: cannot be submitted on userstyles site so commented out, just uncomment it after installing locally
    filter:progid:DXImageTransform.Microsoft.gradient(startColorstr=#99000000, endColorstr=#99000000) !important;
    */
    /* For IE 8*/
    /* NOTE: cannot be submitted on userstyles site so commented out, just uncomment it after installing locally
    -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr=#99000000, endColorstr=#99000000)" important;
    */
}
/* urls on google search page */
.vurls b
{
  color:#006621 !important;
}
/* anchor links - general */
a 
{
  color:#E0E0E0 !important;
  background: #252525 !important;
  border-color:#000 !important;
}
a:visited 
{
  color:#A0A0A0 !important;
  background: #252525 !important;
  border-color:#000 !important;
}
a:hover 
{
  color:#FFFFFF !important;
  background: #252525 !important;
  border-color:#000 !important;
}
a:active 
{
  	color:#FFFFFF !important;
  background: #252525 !important;
  border-color:#000 !important;
}
