css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?:\/\/(www\.)?youtube.com\/watch.+?") {
  body > *:not(#body-container),
  #body-container > *:not(#page-container),
  #page > *:not(#player) { display: none !important; }
  
  #player-api {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    height: auto !important;
    width: auto !important;
  }
}
