css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("explosm.net") {

#meatandbones{
    background: #fff !important;
    padding: 0px !important;
}

body, .container, #maincontent{
    background: #fff !important;
    border-top: #fff !important;
}

.container, #maincontent{
    width:100% !important;
    position: absolute !important;
}

#header, #menubar, #sidebar, #footer, #bottomad{
    display:none !important;
}

#maincontent  > :nth-child(2), #maincontent  > :nth-child(5), #maincontent > :nth-child(3) > :nth-child(3){
    display:none !important;
}

#maincontent  > :nth-child(3) table tbody tr > :nth-child(1){
    display:none !important;
}

#maincontent  > :nth-child(3) > :nth-child(2){
    width: 100% !important;
}

img{
    margin-right:auto !important;
    margin-left:auto!important;   
}

#maincontent  > :nth-child(3) {
    border:none !important;
}

table{
    width: 1px !important;
}
}
