css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.itespresso.es") {
    body{
        background-image:none !important;
        padding-top: 0 !important;
    }
    body{
        padding: 0;
    }
    .header_top,
    .bottom_right,
    .sidebar-container,
    .links,
    #pub_ist_layer, 
    #top_ads_wrap1,
    #sky_right,
    #google_image_div,
    #sidebar-right,
    #footer_wrap,     
    #plista_widget_belowArticle,
    #plista_widget_slide,    
    #pub_ist_fond{
       display:none !important;
    }    
    
    .twoCols #main_col{
       width:100% !important;
    }
    .full-article{
       margin-left: auto !important;
       margin-right: auto !important;
    }
    
    #container,
    #middle_wrap,     
    #middle,
    #main_col,
    .full-article{
       width:100% !important;
    }
    
    .full-article > *{ 
      margin-left: 0px !important;
      margin-right: 40px !important;
}
}
