css:
@-moz-document domain("forums.unknownworlds.com") {
/* Uncomment the following line to show the background image*/
/*.Container
{
  background-color: White;
}
.Overlay-Onos, .Overlay-Soldier
{
  display:none;
}
/**/
.Site-Footer, .PromotedContent ~ .TableRow .Content
{
  background-image: none; 
  background-color: White; 
}
.Container
{
  background-image: none;
}
.Site-Body .Row, .Section-Discussion .Item-Header, .Section-Discussion .Form-HeaderWrap, .Section-Discussion .Item-BodyWrap, .Section-Discussion .Form-BodyWrap, .Site-Breadcrumbs, .CommentForm h2.H
{
  background-color: White;
  box-shadow: none;
}
.Site-Breadcrumbs:before
{
  background-color: White;
  border-color: White;
  box-shadow: none;
}
body
, .Pager .Highlight
, .Pager span
, .Message small
, .Activities li.Condensed .Excerpt
, .PanelActivity li
, .PanelActivity li em
, body blockquote.Quote
, body blockquote.UserQuote
, .quotemain
, .Section-CategoryDiscussionList .CategoryBox .Item
, .DiscussionForm h1.H, .CategoryGroup>h2.H
{
  background-color: #e6e6e6;
}
.DiscussionsTable .Unread .HasNew, .Discussion .PageTitle h1 a, h1.H a, h2.H a, .HomepageTitle a
{
  color: Black;
}
.Discussion .PageTitle h1, h1.H, h2.H, .HomepageTitle 
{
  color: Black;
}
.Section-Discussion .Item-Header .AuthorInfo .MItem, .FormWrapper .Preview .Message
{
  color: Black;
}
body,
.Pager .Highlight,
.Pager span,
.Message small,
.Activities li.Condensed .Excerpt,
.ItemDiscussion .Meta,
.DataList .Meta,
.PanelActivity li,
.PanelActivity li em {
    color: Black;
}
.MiniPager a:hover, .Pager a:hover, .Pager a.Highlight, .MiniPager a, .Pager span, .Pager a
{
  color: Black;
}
.Item.Read, .Item.Announcement, .DiscussionsTable .Item.Read
{
  background-color: #cccccc;
}
a
{
  color: #0066ff;
}
a:hover
{
  color: #38b2ff;
}
.ViewCount
{
    padding-right: 20px !important;
}
.PhotoWrap
{
    -webkit-filter: invert(100%);
}
.PhotoWrap::before
{
    -webkit-filter: invert(100%);
}
.ProfilePhoto
{
    -webkit-filter: invert(100%);
}
  .Section-Discussion .Item-Header .Author .PhotoWrap
, .Section-Discussion .Form-HeaderWrap .Author .PhotoWrap
  , .Section-Discussion .Item-Header .Author .PhotoWrap:after
    , .Section-Discussion .Form-HeaderWrap .Author .PhotoWrap:after
    , .BoxPromoted .Pagination li a
    , .BoxPromoted .Pagination .current a
    , .BoxPromoted .Prev
    , .BoxPromoted .Next
    , .BoxPromoted .Item
    , .PromotedContent
    , .Section-CategoryDiscussionList .CategoryBox:nth-child(odd):after
    , .Section-CategoryDiscussionList .CategoryBox:nth-child(even):after
    , .Section-CategoryDiscussionList .CategoryBox .Item:before
    , .Section-CategoryDiscussionList .CategoryBox .Item:hover:before
    , .Section-CategoryDiscussionList .CategoryBox .ItemContent:before
    , .Section-CategoryDiscussionList .CategoryBox .Item.New .ItemContent:before
    , .Site-Banner .UnknownWorlds-Logo a
    , .Site-UserBox, .Site-UserBox .SignIn
    , .Site-Footer .UnknownWorlds .Logo
    , .PromotedContent .H:before
    , .Section-CategoryDiscussionList .CategoryBox:nth-child(odd) .H:before
    , .Section-CategoryDiscussionList .CategoryBox:nth-child(even) .H:after
    , .Site-Banner .Logo
    , .Site-Banner .UnknownWorlds-Logo
    , .Site-UserBox .SignIn span:after
    , .Site-UserBox .MeBox>.PhotoWrap
    , .Site-UserBox .MeBox .WhoIs .MeMenu
    , .Site-Search .InputBox
    , .Site-Search .InputBox:focus
    , .Site-Search .InputBox:focus+.Button
    , .Site-Search .Button
    , .Site-Overlay .Overlay-Onos
    , .Site-Overlay .Overlay-Soldier
    , .Section-Discussion .Item.Role_NS2Developer .PhotoWrap
    , .Section-Discussion .MessageForm.Role_NS2Developer .PhotoWrap
{
}
  .Site-Navigation .Row
    , .Site-Navigation .Links-Social .Link-Google span
    , .Site-Navigation .Links-Social .Link-Twitter span
    , .Site-Navigation .Links-Social .Link-Facebook span
    , .Site-Navigation .Links-Social .Link-Youtube span
    , .Section-CategoryDiscussionList .CategoryBox .Item:before
    , .Section-CategoryDiscussionList .CategoryBox .Item:hover:before
  {
    -webkit-filter: invert(100%);
  }
}
