css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {

html, body, .code, code {
    background: #002b36 !important;
    color: #658089 !important;
}

#sitetitle {
    background: #b58900 !important;
}
    
h2 a {
    color: #658089 !important;
}
    
.menu a {
    color: #658089 !important;
    text-decoration: none;
}
    
#loginGreating a {
    color: #658089 !important;
    text-decoration: none;
}
    
#sitetitle {
    color: #feedcf !important;
}
    
a {
    color: #2688c9 !important;
}
    
a.visited {
    color: #627f85 !important;
}
    
.nav-buttons a {
    color: #feedcf !important;
}
    
.messages .msg:target {
    background: #073642 !important;
    border: 1px solid #dc322f !important;
    margin-top: 5px;
}

.menu, .infoblock, .boxlet,.messages .msg, .message-table .tag {
    background: #073642 !important;
}

.infoblock, .boxlet, .msg, .message-table .tag {
    border-radius: 0 !important;
}

.msg {
    border-top: 5px solid #002b36 !important;
}

#bd .forum table th, .message-table tr:hover {
    background: #073642 !important;
}

.msg {
    padding-left: 15px !important;
    padding-right: 15px !important;
}

}
