css:
@-moz-document domain("gamescmsadmin.ladbrokes.com") {
.button {
    position: fixed;
    bottom: 30px;
    left: 30px;
    background: rgba(0,0,0,0.75);
    text-align: center;
    border: 4px solid rgba(0,0,0,1);
    width: 90px;
    padding: 5px 10px;
    cursor: default !important;
    height: 32px !important;
    }

.button input:first-child {
    font-size: 18px;
    display: block !important;
    }

.button:hover {
    background: #cccccc;
    border-color: red;
    box-shadow: 3px 3px 3px rgba(50,50,50,0.5);
    }

.button input {
    cursor: pointer !important;
    display: none;
    float: left;
    width: 90px;
}
}
