css:
/* AGENT_SHEET */
scrollbar {
  -moz-appearance: none !important;
  border: none !important;
  background: transparent !important;
}

scrollbar[orient="vertical"] { width: 14px !important; }

scrollbar[orient="horizontal"] { height: 14px !important; }

scrollbar thumb {
  -moz-appearance: none !important;
  border: none !important;
  background: #7D7D7D !important;
  
}

scrollbar thumb:hover { background-color: #999 !important; }

scrollbar thumb:active { background-color: #666 !important; }
