css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xchat.org") {
html, body, #payload, #navdiv a, td {
background:#000 !important;
}
a, p, h1, h2, h3, h4, h5, small, .date, blockquote, td {
color:#FFF !important;
}
}

@-moz-document domain("forum.xchat.org") {

#wrap, #phpbb, .navbar, .icon, .forumbg, .post, .bg2 {
background:#000 !important;
}
a, p, h1, h2, h3, h4, h5, .forumtitle, .icon, .content {
color:#FFF !important;
}
span.corners-bottom span, span.corners-top span, .forumbg {
background-color:#000 !important;
}
}
