css:
/*
Note: This theme only remove ads, banner, not blocking popup.
      Theme này chỉ xóa quảng cáo, banner, không chặn popup.
*/
/* Home */
@-moz-document domain("blogtruyen.com"){
body {background: url(http://i.imgur.com/9A5daDI.jpg) fixed center !important;}
#leftAdsBanner, #bar_container, #after a, #mota, #boxes {display: none !important;}
.logo {font-family: 'Audiowide', cursive; font-weight: bold}
#toolbar {
    background: #444 !important;
    border-bottom: 2px solid #444 !important;
    opacity: 0.85;
}
#toolbar .frm-q-search input[type="text"] {
    background: #999;
    border-radius: 5px;
}
#toolbar .frm-q-search input[type="submit"] {
    background: #000;
    border-radius: 5px;
    color: #FFF;
    transition: all .2s;
}
#toolbar .frm-q-search input[type="submit"]:hover {
    background: #111;
}
#toolbar ul.menu > li:hover > ul {border-bottom-left-radius: 5px; border-bottom-right-radius: 5px; }
.menu li a {transition: all 0.1s}
#main-left > .storyitem footer {
    background: #666;
    border: 1px solid #333;
    color: #FFF !important;
}
#readonline #content img {border-radius: 5px;}
.paging .page a, #pagingHome .page a {background: #222; opacity: .9; transition: all .3s; border-radius: 5px; color: #fff;}
.paging .page a, #pagingHome .page a:hover {background: #444;}
.paging .current_page, #pagingHome .current_page {background: #32C7F0; border-radius: 5px;}
.notification > span {-webkit-filter: grayscale(100%);
filter: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg'><filter id='grayscale' filterRes='600'><feColorMatrix type='matrix' values='0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0'/></filter></svg>#grayscale");}
.avatar img {border-radius: 5px;}
#divNotification, #divTrackingStory {border-color: #444; opacity: 0.9}
/* Login */
.wrapper {border-radius: 10px; opacity: .96}
#frm_dangnhap input[type="submit"] {transition: all .2s; border-radius: 5px; border: 1px solid #1B5BF2 ;}
#frm_dangnhap input[type="submit"]:hover {background: #3AABF7}
/* CP */
.brand span {font-family: 'Audiowide', cursive; font-weight: bold; font-size: 25px}
.row-fluid [class*="span"]:first-child  {background: #FFF;}
.pull-right, .pull-left {color: #FFF}
.btn-primary:hover {background: rgba(14, 114, 174, .8)}
.main-menu {margin-left: 6px; padding-right: 1px}
.navbar .navbar-inner {background: linear-gradient(to top, #222, #444)}
.sidebar-nav, .row-fluid .span12 {box-shadow: 0px 0px 10px #333}
}
