css:
/*==================================================
  webry.info BIGLOBE webry blog
  domain(at.webry.info)
  ==================================================*/
@-moz-document domain('at.webry.info') {
/*--- unwanteds ---*/

/*--- page containers ---*/
body {background: none !important;}
#container {width: auto !important;}

/*--- header ---*/
#banner, #subbanner
 {position: static !important; width: auto !important; height: auto !important;}
#banner h1, #subbanner h1
 {position: static !important; top: inherit !important; left: inherit !important;}

/*--- blog topic 2 columns layout ---*/
#blog {float: left; box-sizing: border-box; width: 100% !important; margin: 0 -16em 0 0 !important;}
#blog>* {width: auto !important; margin: 0 16em 0 0 !important;}
#utilities {width: 16em !important; box-sizing: border-box; margin: 0 !important;}

/*--- blog topic list 2 columns layout ---*/
#contents {float: left; box-sizing: border-box; width: 100% !important; margin: 0 -16em 0 0 !important;}
#contents>* {width: auto !important; margin: 0 16em 0 0 !important;}
#contents #postlist table {width: auto !important;}


/*--- footer ---*/
#footer {width: auto !important; text-align: left !important;}
#footer #links, #footer #links div,
#footer #sbanner, #footer a, #footer a img {
 display: inline !important;
 width: auto !important; height: 1em !important;
 margin: 0 !important; padding: 0 !important;
 }
}
