css:
@-moz-document domain("taringa.net") {
#main {
	width: auto;
  	background-position-x: 265px;
  	overflow: visible;
  	background-image: none !important;
}
#main.homes {
	background-image: none !important;
}
#main-col{
	width: 75%;
}
#main-col #full-col {
	width: auto;
}
#main-col #left-col {
    width: 48%;
}
#main-col #right-col {
	width: 47%;
}
#sidebar {
  	position: absolute;
	right: 0;
	width: 23%;
}
.activity-content, .my-shout-content{
	width: 87%;
}
.activity-element a.avatar, .shout-box a.avatar {
	width: 5%;
}
.image .activity-content img {
	max-width: none;
}
.activity-content a img, .activity-content iframe {
	max-width: 520px;
}
#full-col.post img {
	max-width: none !important;
}
.post-content img {
	max-width: none !important;
}

#footer .wrapper {
 	width: auto;
}
#footer .last-action {
 	text-align: center;
}
#footer .last-action>div {
 	display: inline-block;
    width: 27%;
    float: none;
}
#footer .last-action>div:nth-child(2) {
 	vertical-align: top;
}
.irCielo {
    margin: 0 auto;
    width: 80px;
}
}
