css:
/*======================================================
  twitter home liquid compact 2014/02
  domain(twitter.com)
  ======================================================*/

@-moz-document domain('twitter.com') {
/*--- unwanted ---*/


/*--- containers ---*/
body {font-family: inherit !important;}
html, body, #doc, #page-outer {height: auto !important;}
.container {width: auto !important;}
.wrapper, .wrapper-narrow, .wrapper-permalink {
 width: auto !important;
 min-height: auto !important;
 padding: 54px 4pt !important;
 }

/*--- dashboard on the left side ---*/
.dashboard {width: 200pt !important;}

/*--- profilecard on the top right ---*/
.profile-card {
 position: static !important;
 box-sizing: border-box; width: 100% !important; margin: 0 0 0 -200pt !important;
 }
.profile-card>div
 {width: auto !important; margin: 0 0 0 200pt !important;}
.profile-header-inner-overlay {width: auto !important; height: auto !important;}

/*--- main contents on the right ---*/
.content-main
 {float: right !important; box-sizing: border-box; width: 100% !important; margin: 0 0 0 -200pt !important;}
.content-main>div
 {width: auto !important; margin: 0 0 0 200pt !important;}
}
