css:
@-moz-document domain("thepiratebay.se") {
*, textarea, input[type="text"], input[type="password"]{
  font-family: "Vrinda", "Kartika", monospace, sans-serif;
  scrollbar-base-color: #5093F3 !important;
  scrollbar-face-color: #5093F3 !important;
  scrollbar-track-color: #3093F3 !important;
}
body, textarea, input[type="text"], input[type="password"], input[type="search"], input[type="submit"], select, h2, *{
  color: gray !important;
  background-color: black !important;
}
textarea, input[type="text"], input[type="password"], input[type="search"], input[type="submit"],
a, a:link, a:visited, a:focus, a:-webkit-any-link, select, h2{
  color: green !important;
}
div#fbanners, img#TPBlogo, h1>a, strong#howdl, div#icons{
  display: none !important;
}
textarea, input[type="text"], input[type="password"], input[type="search"], input[type="submit"], button,
table, table#searchResult th, table#searchResult td, div#title, div#detailsframe{
  border-top: 2px solid green !important;
  border-left: 2px solid green !important;
  border-right: 2px solid green !important;
  border-bottom: 2px solid green !important;
}
  
}
