css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

h2, h3 {
    font-size: 16px;
}

.stats a strong {
font-size: 14px;
font-weight: bold;
line-height: 16px;
}

.global-nav,.topbar,.global-nav-inner {
opacity:.95;
background:#222;
transition: all 0.5s ease-out 0.1s;
}

.global-nav:hover,.topbar:hover,.global-nav-inner:hover {
opacity:1;
transition: all 0.5s ease-out 0.1s;
}

.tweet-btn {
padding: 7px 12px 6px 13px;
}

}
