css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

    .global-nav-inner .container { max-width: 890px }
    .topbar-tweet-btn button { margin-right:  0 }
    h1.Icon--bird { display: none !important; }
    
    p.tweet-text { 
        font-family: Georgia, serif !important;
        margin: 0.3em auto;
    }
    
    div.global-nav-inner {
        height: 45px;
    }
    
    .stats strong {
        font-size: 160% !important;
        color: #000 !important;
    }
    
    .user-dropdown {
        padding: 0 10px 0 5px;
    }
    
    span.text {
        color: #fff;
    }
    
    a.js-nav strong {
        color: #000;
    }
    
    a.request-retweeted-popup strong {
        color: #000;
    }
    
    a.request-retweeted-popup {
        color: #000;
    }
    
    .Footer, .trends {
        display: none;
    }
    
}
