css:
@-moz-document url-prefix("http://twitter.com"), url-prefix("https://twitter.com") {
.global-nav-inner{background:#111 !important;}
.bird-topbar-etched {color:#eee !important;}
.pushing-state .pushstate-spinner{background-image:url(https://dl.dropboxusercontent.com/u/203328/loader.gif) !important;}
#global-actions > li > a {border-bottom: 4px solid #eee !important;}
#global-actions > li > a:hover, #global-actions > li > a:focus,.nav.right-actions>li>button:hover, .nav.right-actions>li>a:hover, .nav.right-actions>li>button:focus, .nav.right-actions>li>a:focus {color:#eee !important;}
}
