css:
@-moz-document domain("twitter.com") {
.t1.logged-in {
	background-position: left 0px;
}

.global-nav-inner {
   	background: rgba(17, 17, 17, 0.8);
}

.global-nav-inner .container {
  	width: 70%;
	margin: auto;
}

.nav > li {
    color: #e6e8fa;
}

.wrapper {
    width: 70% !important;
}

.enhanced-mini-profile .mini-profile .profile-summary {
	background-size: cover !important;
}

.dashboard {
    width: 30%;
}

#timeline, .profile-card.profile-header {
    width: 68%;
}

.profile-header-inner-overlay {
	width: 100%;
}
  
.stats a {
    font-size: 13px;
    text-transform: none;
    letter-spacing: .07em;
}

.module .flex-module {
    background-color: #f8f8ff;
    border: 1px solid #f8f8ff;
    padding: 15px;
}

.header-inner, .list-link, .new-tweets-bar, .modal-content, .modal-header, .follow-bar, .dashboard .stats, .home-tweet-box, .rosetta .dm-tweetbox, .rosetta .WebNotification-buffer--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, .tweet:hover, .tweet-actions:hover, .stream-item, .original-tweet-container, .tweet-actions, .recent-tweets, .flex-module, .permalink, .tweet {
	background-color : #f8f8ff !important;
}

.content-header, .list-link, .flex-module, .original-tweet-container, .stream-item, .new-tweets-bar, .modal-header, .tweet, .profile-social-proof{
	border-color : #f8f8ff !important;
}
      
.tweet-actions{
	box-shadow : none !important;
}

.tweet > .context .with-icn {
    margin-left: -59px;
	margin-bottom: 5px;
}
  
}
