css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.freekick.org") {
    /* CSS Document */
    #page-wrap{
        min-width: 940px;
        width:100% !important;
    }
    * html #blue-bar{margin-left:-5px !important;
    }
    #main-green-wrap{position:relative !important;
        width:100% !important;
    }
    #main-content{padding-right:20px !important;
    }
    #content-l{
        float:left !important;
        width:77% !important;
        height:100% !important;
        margin-right:0 !important;
        padding:0 !important;
        font-size:10px !important;
        border-right:0 solid #ccc !important;
        padding-right:0 !important;
    }
    #content-r{
        float:right !important;
        font-size:10px !important;
        width:20% !important;
        height:100%;
        padding:0 10px 0 0 !important;
    }
    #menu-footer {
        width: auto !important;
    }

    #main-green-wrap {
        background-repeat: repeat !important;
    }

    .btm-shadow {
        background-repeat: repeat-x !important;
    }

    table.dotted .data {
        padding-right:0 !important;
        text-align:center !important;
    }

    table.dotted .algnValue,
    table.dotted .algnR,
    table.dotted th {
        text-align:center !important;
        padding-right: 0 !important;
        padding-left: 0 !important;
    }
    .blue-bl, .green-bl {
        width: 100% !important;
    }
}
