/*========================================================
  seesaa BLOG ja jp liquid compact
  domain(seesaa.net)
  ========================================================*/

@-moz-document domain('seesaa.net') {
/*--- unwanteds ---*/
#common-header,
#ad-60days-area,
dummy#dummy.dummy {display: none !important;}


/*--- generic containers ---*/
body, #container {
 background: none !important;
 width: auto !important; min-width: auto !important; max-width: inherit !important;
 height: auto !important; min-height: auto !important; max-height: inherit !important;
 margin: 0 !important; padding: 0 !important;
 font-family: inherit !important;
 }

/*--- header and footer ---*/
#banner, #footer {
 background-size: auto 100% !important;
 box-sizing: border-box;
 width: auto !important; height: auto !important;
 margin: 0 !important;
 }

/*--- main column of 2columns layout ---*/
#content 
 {box-sizing: border-box; width: 100% !important; margin: 0 -15em 0 0 !important;}
#content>*
 {box-sizing: border-box; width: auto !important; margin: 0 15em 0 0 !important;}
#content>* * {max-width: 100% !important; height: auto !important;}

/*--- sub column of 2columns layout ---*/
#links {
 box-sizing: border-box;
 width: 15em !important; margin: 0 !important;
 word-wrap: break-word !important;
 }
#links * {max-width: 100% !important; height: auto !important;}
}
