css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(nytimes.com) {

body, #miniNav, .masthead-button, .editionListContainer, .selected, .comments-button,
#masthead, .collection-menu, .collection-marker, .label, .user-tools, .subNavigation,
.opening, .update {
    background-color:#ddd !important;
}

.quick-navigation .sections-button, .ribbon-page-navigation {
    background-color:#bbb !important;
}

.user-tools {
    box-shadow:none !important;
}
.count {
    color:#ddd !important;
}

.ribbon-page-navigation {
    opacity:.25 !important;
}
.ribbon-page-navigation:hover {
    opacity:1 !important;
}

.collection-menu .active .story-container {
    background-color:#bbb !important;
}

}
