css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dpstream.net") {

body {
background-image: url('http://www.psdgraphics.com/file/colorful-triangles-background.jpg')!important;
background-color:#000000 !important;
background-attachment:fixed;
}

div#top {
background-image: none !important;
}


div#contenu {
width : 100%;
}

#degrade_fond{
background-image : none !important;
}

#affiche_fond{
background-image : none !important;
}



input, select, textarea{
background-color : transparent !important;
}

.b1 { background-color:#444444!important; }
.b2,.b3,.tgg,.tggm,.tah,.tah th,h1,
.top_menu_droite,.top_menu_gauche,#conteneur{ border-color:#444444!important; }

.tggm,.tah .tggm {border-top: 1px rgba(0, 0, 0, 0) !important;}
.rr1 .bas .b2,.rr1 .bas .b3,
.rr2 .bas .b2,.rr2 .bas .b3,
.ras4 .bas .b2,.ras4 .bas .b3,
.ram4 .bas .b2,.ram4 .bas .b3,
.rsm2 .bas .b2,.rsm2 .bas .b3,
.rsm3 .bas .b2,.rsm3 .bas .b3,
.rmt .bas .b2,.rmt .bas .b3,
.titre_h1 .bas .b2,.titre_h1 .bas .b3 { background: rgba(0, 0, 0, 0.6) !important;}
.tgg,h1,th { background: rgba(0, 0, 0, 0.6) !important;}
.haut .b2,.haut .b3 { background: rgba(0, 0, 0, 0.6) !important;}
.tggm ,.bas .b2,.bas .b3,
.rsm1 .haut .b2,.rsm1 .haut .b3,
.rsm4 .haut .b2,.rsm4 .haut .b3,
.rf3 .haut .b2,.rf3 .haut .b3 { background: rgba(0, 0, 0, 0.6) !important;}
#conteneur,.conteneur .b2,.conteneur .b3 { background: rgba(0, 0, 0, 0.2) !important;}
.table_fond {background: rgba(0, 0, 0, 0.0) !important;}

th.top_menu{
border-right: 1px rgba(0, 0, 0, 0) !important;
border-left: 1px rgba(0, 0, 0, 0) !important;
}
th.top_menu_droite{
border-left: 1px rgba(0, 0, 0, 0) !important;
}
th.top_menu_gauche{
border-right: 1px rgba(0, 0, 0, 0) !important;
}


/*Modification de Gautier */


.rmt {
display : none;
}
  
#menu {
display : none;
}
  
.raccueil1 {
display : none;
}
    
.raccueil2 {
 display : none;
}

.ras3 {
display : none ;
}

a:link:hover {
background : none;
color : #A0A0A0;
}

h3, th, .tgg div{
color: #FF4444;
}

a b{
color: #FF4444;
}


/*Cadres à l'intérieur des vidéos */

div#contenu div.tggm * > div.cadre * {
    background-color: transparent !important;
    color: lightgrey !important;
    font-size: 11px !important;
}

div#contenu div.tggm * > div.cadre div {
    font-style: italic !important;
}

div#contenu div.tggm * > div.cadre u {
    padding: 0.6em !important;
}

div#contenu div.tggm * > div:not(.cadre) > b * {
    border: 50px !important;
    color: #0099FF !important;
    font-size: 15px !important;
}

div#contenu div.tggm *:not(.cadre) > div:not(.cadre) > a {
    border-width: 0px !important;
    font-size: 0px !important;
}

div#contenu div.tggm *:not(.cadre) > div:not(.ep):not(.cadre) {
    border-width: 0px !important;
    font-size: 0px !important;
}


div#contenu div.tggm > div:not(.cadre) a {
    font-size: 15px !important;
}

}
